<script>
import { mapGetters } from "vuex";
export default {
  name: "BaseChatMessage",
  props: {
    description: {
      type: String
    },
    type: {
      type: String
    }
  },
  computed: {
    isAnswer() {
      return this.type === "answer";
    },
    ...mapGetters("user", ["user_photo", "user_name"])
  }
};
</script>

<style lang="scss">
.c-help-chat {
  &__message {
    background-color: $white;
    padding: 16px;
    max-width: 327px;
    margin-bottom: 13px;
    &.question {
      margin-left: auto;
      border-radius: 10px 10px 0px 10px;
      .c-help-chat {
        &__img {
          border-radius: 50%;
          overflow: hidden;
          width: 32px;
          height: 32px;
          border: 1px solid $red;
        }
      }
    }
    &.answer {
      border-radius: 10px 10px 10px 0px;
    }
  }
  &__user {
    &-img {
      @extend %df;
      @extend %aic;
      margin-bottom: 10px;
    }
    &-name {
      @extend %btn-text;
      color: $gray;
      margin-left: 10px;
    }
  }
}
</style>
